import React, { Component, useState, useEffect } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'

const Queqed = (props) => {
    let [queq,setQueq] = useState();
    const info = props.location.state;
    () => setQueq(queq = info.currQ);
    // console.log(queq);
    // console.log(info);
    // const reducingQ = () => {
    //     setInterval(() => {
    //         info.currQ = info.currQ-1;
    //         console.log(info.currQ);
    //     },3000);
    // };
    // reducingQ();
    return(
        <Wrapper>
            <WrapperCards>
                <HeaderText>YOU ARE IN QUEQ!</HeaderText>
                <CardText>Name : {info.name[0]}</CardText>
                <CardText>Place : {info.place[0]}</CardText>
                <CardText>Queq : {info.currQ}</CardText>
            </WrapperCards>
            {/* <HeaderText>YOU ARE IN QUEQ!</HeaderText> */}
        </Wrapper>
    );
}

export default Queqed

const Wrapper = styled.div`
display: flex;
flex-direction: column;
align-items: center; 
justify-content: center;
width: 100%;
height: 100vh;
`;

const HeaderText = styled.span`
font-weight: bold;
font-size: 3rem;
color: linear-gradient(90deg, rgba(184,52,4,1) 0%, rgba(200,16,16,1) 27%, rgba(255,0,39,1) 100%);
@media (max-width: 768px) {
    font-size: 1.8rem;
}
`;

const CardText = styled.span`
/* font-weight: bold; */
font-size: 1.5rem;
color: linear-gradient(90deg, rgba(184,52,4,1) 0%, rgba(200,16,16,1) 27%, rgba(255,0,39,1) 100%);
@media (max-width: 768px) {
    font-size: 1.8rem;
}
`;

const WrapperCards = styled.div`
display: flex;
flex-direction: column;
/* align-items: center; */
justify-content: center;
border-radius: 25px;
box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
padding: 30px;
margin: 16px;
color: #FFFFFF;
background: linear-gradient(90deg, rgba(184,52,4,1) 0%, rgba(200,16,16,1) 27%, rgba(255,0,39,1) 100%);
`;
