import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'
import Mc from '../Assets/Mc.png'
class Cards extends React.Component {
    constrctor(props){
        // super(props)
        this.state = {
            name: []
        };
    }
    render(){
        return(
            <Wrapper>
                <Logo src={Mc}/>
                <TextContainner>
                <Texts>McDonald</Texts>
                <Texts>Place : Central Rama 2</Texts>
                <Texts>Queq : 46</Texts>
                <Button>Reserve</Button>
                </TextContainner>
            </Wrapper>
        );
    }
}

export default Cards

const Wrapper = styled.div`
display: flex;
/* align-items: center; */
/* justify-content: center; */
border-radius: 25px;
box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
width: 33%;
margin: 16px;
`;

const TextContainner = styled.div`
display: flex;
flex-direction: column;
/* align-items: center; */
/* justify-content: center; */

padding: 16px;
`;

const Texts = styled.span`
font-size: 1.2rem;
margin: 8px 0 8px 0;
`;

const Button = styled.button`
margin-top: auto;
height: 50px;
border: none;

background: linear-gradient(90deg, rgba(184,52,4,1) 0%, rgba(200,16,16,1) 27%, rgba(255,0,39,1) 100%);
color: #FFFFFF;
font-weight: bold;
`;
const Logo = styled.img`
margin-right: 8px;
padding: 12px;
width: 200px;
border-radius:  25px 0 0 25px ;
background: linear-gradient(90deg, rgba(211,235,9,1) 0%, rgba(199,5,5,1) 52%, rgba(222,30,30,1) 100%);
`;
