import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'
import profile from '../Assets/Profile.jpg'
import TestProps from './testProps.js'

class Main extends React.Component {
    constructor(props){
        super(props)
        this.state = {
          testValue : 1,
        };
    }
    onReverse = (amount) => {
        this.setState({
            testValue : this.state.testValue+amount,
        });
    }
    render() {
        return (
        <Wrapper>
            <ProfileContainer>
                
                {/* <ProfileImg src={profile} /> */}
                <TestProps testValue = {this.state.testValue} onReverse = {this.onReverse}/>
            </ProfileContainer>
        </Wrapper>
        )
    }
}
  
export default Main

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
width: 100%;
`;

const ProfileContainer = styled(FlexboxCenter)`
height: 300px;
width: 100%;
/* background-color: #000000; */
`;

const ProfileImg = styled.img`

`;
