import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'
import bgSearch from '../Assets/1.png'
import Cards from './cards.js'
import TestProps2 from './testProps2.js'

class Main extends React.Component {
    constructor(props){
        super(props)
        this.state = {
          searchInput : "",
        };
    }
    // sendProps = () => {
    //     this.setState({
    //         parentValue : "success"
    //     });
    // }
    setSearch = (e) => {
        this.setState({
            searchInput : e
        });
        console.log(e);
    }
    render() {
        return (
        <Wrapper>
            <SearchBg src={bgSearch}/>
            <SearchContainner>
                <Search placeholder="Find Dinner Place."
                        value={this.state.searchInput}
                        onChange={(e) => this.setSearch(e.target.value)}/>
                <SearchBtn>SEARCH</SearchBtn>
            </SearchContainner>
            {/* <Cards></Cards> */}
        </Wrapper>
        )
    }
}
  
export default Main

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
width: 100%;
`;

const SearchContainner = styled(FlexboxCenter)`
height: 300px;
width: 100%;
`;

const SearchBg = styled.img`
opacity: 0.5;
position: absolute;
height: 300px;
width: 100%;
object-fit: cover;
z-index: -1;
`;

const Search = styled.input`
width: 100%;
height: 30px;
border: none;
  text-indent: 10px;
::placeholder {
  text-indent: 10px;
}
`;

const SearchBtn = styled.button`
width: 10%;
height: 33px;
border: none;
background: linear-gradient(90deg, rgba(184,52,4,1) 0%, rgba(200,16,16,1) 27%, rgba(255,0,39,1) 100%);
color: #FFFFFF;
font-weight: bold;
`;
const ProfileImg = styled.img`

`;

const Card = styled.div`
height: 200px;
width: 300px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
background-color: #CCCCCC;
border-radius: 25px;
box-shadow: 5px 10px;
margin: 20px;
`;
