import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'

class TestProps extends React.Component {
    constrctor(props){
        // super(props)
        this.state = {
            childProps : "ready"
        };
    }
    render(){
        let propsColor = this.props.parentValue === "success" ? '#E07667' : "#000000";
        return(
            <div style={{
                height: "200px",
                width: "300px",
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                backgroundColor: "#CCCCCC",
                borderRadius: "25px",
                boxShadow: "5px 10px",
                margin: "20px"
            }}>
                <span>First child</span>
                <button onClick={() => this.props.sendToParentProps}>Parent Value</button>
                <span style={{
                    color : propsColor
                }}>{this.props.parentValue}</span>
            </div>
        );
    }
}

export default TestProps

const TextProps = styled.span`
color: ${props => props.parentValue == "success" ? '#E07667' : "#FFFFFF"}
`;
