import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'
import bgSearch from '../Assets/1.png'
import TestProps from './testProps.js'
import TestProps2 from './testProps2.js'

class Main extends React.Component {
    constructor(props){
        super(props)
        this.state = {
          searchInput : '',
        };
    }
    // sendProps = () => {
    //     this.setState({
    //         parentValue : "success"
    //     });
    // }
    searchHandler = (e) => {
        this.setState({
            searchInput : e
        });
    }
    render() {
        return (
        <Wrapper>
            <SearchBg src={bgSearch}/>
            <SearchContainner>
                <Search placeholder="Find Dinner Place."
                        value={this.props.searchInput}
                        onChange={(e) => searchInp(e.target.value)}/>
            </SearchContainner>
        </Wrapper>
        )
    }
}
  
export default Main

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
width: 100%;
`;

const SearchContainner = styled(FlexboxCenter)`
height: 300px;
width: 100%;
`;

const SearchBg = styled.img`
opacity: 0.5;
position: absolute;
height: 300px;
width: 100%;
object-fit: cover;
`;

const Search = styled.input`
width: 80%;
height: 30px;
`;

const ProfileImg = styled.img`

`;

const Card = styled.div`
height: 200px;
width: 300px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
background-color: #CCCCCC;
border-radius: 25px;
box-shadow: 5px 10px;
margin: 20px;
`;
