import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'
import profile from '../Assets/Profile.jpg'
import TestProps from './testProps.js'
import TestProps2 from './testProps2.js'

class Main extends React.Component {
    constructor(props){
        super(props)
        this.state = {
          parentValue : "ready",
          childValue : "ready",
          secondChildValue : "ready",
        };
    }
    // sendProps = () => {
    //     this.setState({
    //         parentValue : "success"
    //     });
    // }
    render() {
        let propsColor = this.state.childValue === "success" ? '#E07667' : "#000000";
        // console.log(propsColor);
        return (
        <Wrapper>
            <ProfileContainer>
                
                {/* <ProfileImg src={profile} /> */}
                <TestProps parentValue = {this.state.parentValue} 
                           sendToParentProps = {this.sendToParentProps}
                           sendC2CProps = {this.sendC2CProps}
                />
                <Card>
                    <span>Parent</span>
                    <button onClick={this.sendProps}>Send value to Child1</button>
                    <span style={{
                        color : propsColor
                    }}>
                        {this.state.childValue}
                    </span>
                </Card>
                <TestProps2 secondChildValue = {this.state.secondChildValue}
                            resetProps = {this.resetProps}/>
            </ProfileContainer>
        </Wrapper>
        )
    }
}
  
export default Main

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
width: 100%;
`;

const ProfileContainer = styled(FlexboxCenter)`
height: 300px;
width: 100%;
/* background-color: #000000; */
`;

const ProfileImg = styled.img`

`;

const Card = styled.div`
height: 200px;
width: 300px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
background-color: #CCCCCC;
border-radius: 25px;
box-shadow: 5px 10px;
margin: 20px;
`;
