import React, { Component } from 'react'
import styled from 'styled-components'
import { FlexboxCenter } from '../StyledCom/flexbox.js'

class TestProps extends React.Component {
    
    render(){
        let propsTextColor = this.props.parentValue == "success" ? '#E07667' : "#FFFFFF"
        return(
            <div style={{
                height: "200px",
                width: "300px",
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                backgroundColor: "#CCCCCC",
                borderRadius: "25px",
                boxShadow: "5px 10px",
                margin: "20px"
            }}>
                <span>First child</span>
                <button onClick={() => this.props.childToParent()}>Parent Value</button>
                <textProps>{this.props.parentValue}</textProps>
            </div>
        );
    }
}

export default TestProps

const textProps = styled.span`
color: propsTextColor;
`;
